package com.example.acer.transitions_everywhere.preferences;import android.content.Context;import android.content.SharedPreferences;/** * Created by Acer on 19.08.2016. */public class PrefsHelper {    public final static String BUTTONS = "buttons";    public final static String PLAYED_GAMES = "played_games";    public final static String CORRECT_ANS = "correct_answers";    public final static String INCORRECT_ANS = "incorrect_answers";    private SharedPreferences sharedPreferences;    private SharedPreferences.Editor editor;    private static PrefsHelper instance;    public PrefsHelper(Context context) {        instance = this;        sharedPreferences = context.getSharedPreferences(context.getPackageName(), Context.MODE_PRIVATE);        editor = sharedPreferences.edit();    }    public void savePref(String key, Object value) {        if (value instanceof Boolean) {            editor.putBoolean(key, (Boolean) value);        } else if (value instanceof Integer) {            editor.putInt(key, (Integer) value);        } else if (value instanceof Long) {            editor.putLong(key, (Long) value);        } else if (value instanceof String)            editor.putString(key, (String) value);        editor.commit();    }    public static PrefsHelper getPrefsHelper() {        return instance;    }    public String getString(String key) {        return sharedPreferences.getString(key, null);    }    public int getInt(String key) {        return sharedPreferences.getInt(key, 0);    }    public boolean getBoolean(String key) {        return sharedPreferences.getBoolean(key, false);    }}